let connection=null;setupConnection=()=>{connection=(new signalR.HubConnectionBuilder).withUrl("/notificationhub").configureLogging(signalR.LogLevel.Information).build();connection.start().catch(n=>console.error(n.toString()));connection.on("BroadcastTextMessage",n=>{$.growl.notice({title:"Notification",message:n})});connection.on("finished",function(){connection.stop()})};setupConnection();